class Solution:
    def paintWalls(self, cost: List[int], time: List[int]) -> int:
        n = len(cost)
        # Initialize dp array where dp[j] represents the minimum cost to paint j walls
        dp = [sys.maxsize] * (n + 1)
        dp[0] = 0  # Base case: 0 cost to paint 0 walls

