class Solution:
    def canTraverseAllPairs(self, nums: List[int]) -> bool:
        if len(nums) == 1:
            return True

        max_num = max(nums)
        is_prime = [True] * (max_num + 1)
        is_prime[0] = is_prime[1] = False
        for i in range(2, int(math.sqrt(max_num)) + 1):
            if is_prime[i]:
                for j in range(i * i, max_num + 1, i):
                    is_prime[j] = False

        prime_factors = defaultdict(list)
        for num in range(2, max_num + 1):
            if is_prime[num]:
                for multiple in range(num, max_num + 1, num):
                    prime_factors[multiple].append(num)

        parent = {}

        def find(x):
            if x not in parent:
                parent[x] = x
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            root_x = find(x)
            root_y = find(y)
            if root_x != root_y:
                parent[root_y] = root_x

        for num in nums:
            factors = prime_factors[num]
            if not factors:
                return False
            first_factor = factors[0]
            for factor in factors[1:]:
                union(first_factor, factor)

        root = find(prime_factors[nums[0]][0])
        for num in nums:
            if find(prime_factors[num][0]) != root:
                return False

        return True

