class Solution:
    def maximumJumps(self, nums: List[int], target: int) -> int:
        n = len(nums)
        if n == 0:
            return -1

        dist = [-1] * n
        dist[0] = 0  # Starting at index 0 with 0 jumps
        queue = deque([0])

        while queue:
            current = queue.popleft()
            for next_idx in range(current + 1, n):
