    goal = None
    candies = []

    for i in range(H):
        for j in range(W):
            if grid[i][j] == 'S':
                start = (i, j)
            elif grid[i][j] == 'G':
                goal = (i, j)
            elif grid[i][j] == 'o':
                candies.append((i, j))

    directions = [(-1, 0), (0, 1), (1, 0), (0, -1)]
    C = len(candies)
    candy_pos_to_idx = { (i, j): idx for idx, (i, j) in enumerate(candies) }

    visited = [[[False] * (1 << C) for _ in range(T + 1)] for _ in range(H) for _ in range(W)]
    queue = deque()

    si, sj = start
    queue.append((si, sj, 0, 0))
    visited[si][sj][0][0] = True

    max_candies = -1

    while queue:
        i, j, t, mask = queue.popleft()

        if (i, j) == goal:
            max_candies = max(max_candies, bin(mask).count('1'))
            continue

        if t >= T:
            continue

        for dx, dy in directions:
            ni, nj = i + dx, j + dy
            if 0 <= ni < H and 0 <= nj < W and grid[ni][nj] != '#':
                new_t = t + 1
                new_mask = mask

                if (ni, nj) in candy_pos_to_idx:
                    idx = candy_pos_to_idx[(ni, nj)]
                    if not (mask & (1 << idx)):
                        new_mask |= (1 << idx)

