import sys
input = sys.stdin.read

class Solution:
    def main(self):
        data = input().split()
        idx = 0
        N = int(data[idx])
        idx += 1
        Q = int(data[idx])
        idx += 1

        parent = list(range(N + 1))
        rank = [1] * (N + 1)
        degree = [0] * (N + 1)
        isolated_count = N

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]
                u = parent[u]
            return u

        def union(u, v):
            u_root = find(u)
            v_root = find(v)
            if u_root == v_root:
                return False
            if rank[u_root] < rank[v_root]:
                parent[u_root] = v_root
                rank[v_root] += rank[u_root]
            else:
                parent[v_root] = u_root
                rank[u_root] += rank[v_root]
            return True

        def reset_vertex(v):
            nonlocal isolated_count
            # Save the current root of v
            root_v = find(v)
            # Collect all nodes in the component of v
            component = set()
            stack = [v]
            visited = set()
            while stack:
                node = stack.pop()
                if node in visited:
                    continue
                visited.add(node)
                component.add(node)
                for u in range(1, N + 1):
                    if find(u) == root_v:
                        stack.append(u)
            # Remove all edges from v
            for u in component:
                if u != v:
                    degree[u] -= 1
                    if degree[u] == 0:
                        isolated_count += 1
            degree[v] = 0
            for u in component:
                if u != v:
                    if degree[u] == 0:
                        isolated_count -= 1
                    degree[u] += 1
            # Reset the parent of all nodes in the component to themselves
            for u in component:
                parent[u] = u
                rank[u] = 1

        for _ in range(Q):
            query_type = int(data[idx])
            idx += 1
            if query_type == 1:
                u = int(data[idx])
                idx += 1
                v = int(data[idx])
                idx += 1
                if find(u) != find(v):
                    union(u, v)
                    degree[u] += 1
                    degree[v] += 1
                    if degree[u] == 1:
                        isolated_count -= 1
                    if degree[v] == 1:
                        isolated_count -= 1
            else:
                v = int(data[idx])
                idx += 1
                if degree[v] > 0:
                    reset_vertex(v)
            print(isolated_count)

Solution().main()

