def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    D = [[0] * N for _ in range(N)]
    index = 1

    for i in range(N):
        for j in range(i + 1, N):
            D[i][j] = int(data[index])
            D[j][i] = int(data[index])
            index += 1

    # DP table: dp[mask][i] = max weight for matching nodes in mask ending at i
    dp = [[-1] * N for _ in range(1 << N)]

    for i in range(N):
        dp[1 << i][i] = 0

    for mask in range(1 << N):
        for i in range(N):
            if not (mask & (1 << i)):
                continue
            if dp[mask][i] == -1:
                continue
            for j in range(N):
                if mask & (1 << j):
                    continue
                new_mask = mask | (1 << j)
                if dp[new_mask][j] < dp[mask][i] + D[i][j]:
                    dp[new_mask][j] = dp[mask][i] + D[i][j]

    result = 0
    for i in range(N):
        if dp[(1 << N) - 1][i] > result:
            result = dp[(1 << N) - 1][i]
    print(result)

if __name__ == "__main__":
    main()

