import sys

def is_one_insertion(s, t):
    if len(s) != len(t) + 1:
        return False
    i = 0
    j = 0
    while i < len(s) and j < len(t):
        if s[i] == t[j]:
            i += 1
            j += 1
        else:
            i += 1
    return i == len(s) and j == len(t)

def is_one_deletion(s, t):
    if len(s) != len(t) - 1:
        return False
    i = 0
    j = 0
    while i < len(s) and j < len(t):
        if s[i] == t[j]:
            i += 1
            j += 1
        else:
            j += 1
    return i == len(s) and j == len(t)

def is_one_substitution(s, t):
    if len(s) != len(t):
        return False
    diff = 0
    for a, b in zip(s, t):
        if a != b:
            diff += 1
            if diff > 1:
                return False
    return diff == 1

def main():
