input = sys.stdin.read

class SegmentTree:
    def __init__(self, data):
        self.n = len(data)
        self.size = 1
        while self.size < self.n:
            self.size <<= 1
        self.tree = [0] * (2 * self.size)
        self.lazy = [0] * (2 * self.size)
        # Initialize leaves
        for i in range(self.n):
            self.tree[self.size + i] = 1 if data[i] == '1' else 0
        # Build the tree
        for i in range(self.size - 1, 0, -1):
            self.tree[i] = self.tree[2 * i] + self.tree[2 * i + 1]

    def push(self, node, l, r):
        if self.lazy[node] != 0:
            # Apply flip to the current node
            self.tree[node] = (r - l + 1) - self.tree[node]
            # Propagate to children if not a leaf
            if l != r:
                self.lazy[2 * node] ^= 1
                self.lazy[2 * node + 1] ^= 1
            self.lazy[node] = 0

    def flip(self, a, b):
        def _flip(node, l, r, a, b):
            self.push(node, l, r)
            if a > r or b < l:
                return
            if a <= l and r <= b:
                self.tree[node] = (r - l + 1) - self.tree[node]
                self.lazy[node] ^= 1
                return
            mid = (l + r) // 2
            _flip(2 * node, l, mid, a, b)
            _flip(2 * node + 1, mid + 1, r, a, b)
            self.tree[node] = self.tree[2 * node] + self.tree[2 * node + 1]
        _flip(1, 0, self.size - 1, a, b)

    def count_transitions(self, a, b):
        res = 0
        def _count(node, l, r, a, b):
            nonlocal res
            self.push(node, l, r)
            if a > r or b < l:
                return
            if a <= l and r <= b:
                # Count transitions in this segment
                if l != r:
                    left = self.tree[2 * node]
                    right = self.tree[2 * node + 1]
                    if left > 0 and right > 0:
                        res += 1
                    elif left == 0 and right == 0:
                        res += 0
                    else:
                        res += 1
                return
            mid = (l + r) // 2
            _count(2 * node, l, mid, a, b)
            _count(2 * node + 1, mid + 1, r, a, b)
        _count(1, 0, self.size - 1, a, b)
        return res

def main():
    data = input().split()
    idx = 0
    N = int(data[idx])
    idx += 1
    Q = int(data[idx])
    idx += 1
    S = data[idx]
    idx += 1

    st = SegmentTree(S)

    output = []

    for _ in range(Q):
