import math
from collections import defaultdict

def square_free_factor(x):
    if x == 0:
        return 0
    i = 2
    factors = {}
    while i * i <= x:
        count = 0
        while x % i == 0:
            count += 1
            x //= i
        if count % 2 == 1:
            factors[i] = 1
        i += 1
    if x > 1:
        factors[x] = 1
    res = 1
    for p in factors:
        res *= p
    return res

def main():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    A = list(map(int, data[1:N+1]))

    freq = defaultdict(int)
    for num in A:
        sf = square_free_factor(num)
        freq[sf] += 1

    ans = 0
    for count in freq.values():
        if count >= 2:
            ans += count * (count - 1) // 2

    zero_count = freq[0]
    ans += zero_count * (zero_count - 1) // 2

    print(ans)

